package com.norconex.crawler.core.pipeline.importer;

import com.norconex.crawler.core.crawler.CrawlerEvent;
import com.norconex.crawler.core.doc.CrawlDocState;
import com.norconex.crawler.core.fetch.FetchDirective;
import lombok.NonNull;

/**
 * Provided both document checksum and deduplication are enabled,
 * verify there are no other documents with the same metadata checksum.
 * This is not 100% fool-proof due to concurrency but should capture
 * the vast majority of duplicates. Invoked only
 * once (even if both metadata and document directives are enabled).
 */
public class MetadataDedupStage extends AbstractImporterStage {
    @java.lang.SuppressWarnings("all")
    @lombok.Generated
    private static final org.slf4j.Logger LOG = org.slf4j.LoggerFactory.getLogger(MetadataDedupStage.class);

    public MetadataDedupStage(@NonNull FetchDirective fetchDirective) {
        super(fetchDirective);
        java.util.Objects.requireNonNull(fetchDirective, "fetchDirective is marked non-null but is null");
    }

    @Override
    protected boolean executeStage(ImporterPipelineContext ctx) {
        if (!ctx.isFetchDirectiveEnabled(getFetchDirective()) || ctx.isMetadataDirectiveExecuted(getFetchDirective())) {
            return true;
        }
        var docRecord = ctx.getDocRecord();
        var store = ctx.getCrawler().getDedupMetadataStore();
        if (store == null || docRecord.getMetaChecksum() == null) {
            return true;
        }
        var ref = store.find(docRecord.getMetaChecksum());
        if (ref.isPresent()) {
            if (LOG.isDebugEnabled()) {
                LOG.debug("REJECTED duplicate metadata checkum found for: {}", docRecord.getReference());
            }
            docRecord.setState(CrawlDocState.REJECTED);
            ctx.fire(CrawlerEvent.builder().name(CrawlerEvent.REJECTED_DUPLICATE).source(ctx.getCrawler()).subject(ref.get()).crawlDocRecord(docRecord).message("A document with the same metadata checksum " + "was already processed: " + ref.get()).build());
            return false;
        }
        store.save(docRecord.getMetaChecksum(), docRecord.getReference());
        return true;
    }
}
